inline bool check(const int &k) {
  if ((a[n - 1] <= k && a[n] <= k) || (a[n] <= k && a[n + 1] <= k)) return 1;
  if ((a[n - 1] > k && a[n] > k) || (a[n] > k && a[n + 1] > k)) return 0;
  for (int i = 1; i < n - 1; i++) {
    if ((a[n + i] <= k && a[n + i + 1] <= k) ||
        (a[n - i] <= k && a[n - i - 1] <= k))
      return 1;
    if ((a[n + i] > k && a[n + i + 1] > k) ||
        (a[n - i] > k && a[n - i - 1] > k))
      return 0;
  }
  return a[1] <= k;
}

