int solve2(vector<int> a) {
	static int dp[2][ten(5) * 2 - 1];
	FOR(i, sz(a)) dp[0][i] = a[i];
	auto nt = dp[1], pv = dp[0];
	int len = sz(a);
	int st = 0;
	while (len > 1 && st < 1000) {
		len -= 2;
		FOR(i, len) {
			nt[i] = md(pv[i], pv[i + 1], pv[i + 2]);
		}
		st++;
		swap(pv, nt);
	}

	if (len == 1) return pv[0];
	if (pv[len / 2] == pv[len / 2 - 1]) return pv[len / 2];
	if (pv[len / 2] == pv[len / 2 + 1]) return pv[len / 2];

	// return pv[len / 2];

	int l = 0, r = len - 1;
	const int md = len / 2;
	FOR(i, len - 1) {
		if (nt[i] == nt[i + 1]) {
			if (i <= md) l = i + 1;
			if (md < i) r = i;
		}
	}

	bool ok = true;
	for (int i = l; i < r - 1; i++) {
		if (pv[i] < pv[i + 1]) ok = false;
	}
	if (ok) return pv[md];
	ok = true;
	for (int i = l; i < r - 1; i++) {
		if (pv[i] > pv[i + 1]) ok = false;
	}
	if (ok) return pv[md];
	int ld = md - l, rd = r - md;
	if (ld < rd) return pv[l];
	else return pv[r];

	return pv[0];
}

