const int kN = 2e5 + 5;

int n, a[kN];
bool Check(int mid) {
	bool ret = (a[1] > mid), pos = n;
	for(int i = 2; i <= n; ++i)
		if((a[i] > mid) == (a[i - 1] > mid)) pos = n - i, ret = (a[i] > mid);
	for(int i = n + 1; i <= n + pos; ++i)
		if((a[i] > mid) == (a[i - 1] > mid)) pos = i - 1 - n, ret = (a[i] > mid);
	return ret;
}

int main() {
	scanf("%d", &n);
	for(int i = 1; i < 2 * n; ++i) scanf("%d", a + i);
	int l = 1, r = n;
	while(l < r) {
		int mid = (l + r) >> 1;
		if(Check(mid)) l = mid + 1;
		else r = mid;
	}
	printf("%d", l);
	return 0;
}

