	while(r-l>1)
	{
		int m=(l+r)/2;
		int ans=0;
		for(int i=0;i<2*n-1;i++)
			if(aa[i]>=m)a[i]=1;
			else a[i]=0;
		if(a[n-1]==1&&(a[n-2]==1||a[n]==1))ans=1;
		else if(a[n-1]==0&&(a[n-2]==0||a[n]==0))ans=0;
		else
		{
			int ans1=n-1,ans2=n-1;
			while(ans1&&a[ans1-1]!=a[ans1])ans1--;
			while(ans2<2*n-1&&a[ans2+1]!=a[ans2])ans2++;
			if(ans1==0&&ans2==2*n-1)ans=a[0];
			else if(a[ans1]==a[ans2])ans=a[ans1];
			else if(n-1-ans1>ans2-n+1)ans=a[ans2];
			else ans=a[ans1];
		}
