bool small(int i, int j, int k) {return a[i]<=k && a[j]<=k;}  
bool big(int i, int j, int k) {return a[i]>k && a[j]>k;}  
bool judge(int k)  
{  
    for(int i=0; i<n-1; ++i)  
    {  
        if(big(n+i, n+i+1, k) || big(n-i, n-i-1, k)) return false;  
        if(small(n+i, n+i+1, k) || small(n-i, n-i-1, k)) return true;  
    }  
    return small(1,1,k);  
}  
int main()  
{  
    scanf("%d",&n);  
    for(int i=1; i<n<<1; ++i)  
        scanf("%d",&a[i]);  
    int l=1, r=(n<<1)-1, mid;  
    while(l < r)  
    {  
        mid = l+r>>1;  
        if(judge(mid))  
            r = mid;  
        else  
            l = mid + 1;  
    }  
    printf("%d\n",r);  
    return 0;  
}

#include <bits/stdc++.h>
using namespace std;
const int N = 200005;
int n , m , a[N] , b[N];

bool check(int v) {
    //cout << v << endl;
    for (int i = 1 ; i <= m ; ++ i) {
        b[i] = (a[i] > v);
    }
    if (b[n] == b[n - 1] || b[n] == b[n + 1]) {
        return b[n];
    } else {
        int w = b[n];
        for (int i = 1 ; i <= m ; ++ i) {
            b[i] ^= w;
        }
        int l = n;
        while (l > 1 && b[l] != b[l - 1]) {
            -- l;
        }
        int r = n;
        while (r < m && b[r] != b[r + 1]) {
            ++ r;
        }
        if (l <= 1 && r >= m) {
            return ((n + 1) & 1) ^ w;
        } else {
            if (n - l < r - n) {
                return b[l] ^ w;
            } else {
                return b[r] ^ w;
            }
        }
    }
}

