	vector<int> new_layer;
	int state=0,lstate=0;
	for(int i=1;i<layer*2+1;i++)
	{
		state=(nums[i]>nums[i-1]);
		if(i>1)
			if(lstate==state)
				new_layer.push_back(nums[i-1]);
			else if(lstate==0)
				new_layer.push_back(min(nums[i],nums[i-2]));
			else	new_layer.push_back(max(nums[i],nums[i-2]));
		lstate=state;
	}
	nums=new_layer;
	solve(layer-1);
	return ;
}
