using namespace std;
int n;
const int N = 200000;
int ai[N];
int check(int t)
{
    if (ai[n] >= t && (ai[n + 1] >= t || ai[n - 1] >= t)) return 1;
    if (ai[n] < t && (ai[n + 1] < t || ai[n - 1] < t)) return 0;
    int ok = 1;
    for (int i = 1; i < n * 2; i += 2) if (ai[i] < t) ok = 0;
    if (ok) return 1;
    ok = 1;
    int l = n, r = n;
    while ((ai[l] >= t) ^ (ai[l - 1] >= t)) l --;
    while ((ai[r] >= t) ^ (ai[r + 1] >= t)) r ++;
    int L = 0, R = 0;
    if (ai[l] >= t && ai[l - 1] >= t) L = 1;
    if (ai[r] >= t && ai[r + 1] >= t) R = 1;
    if (L && R) return 1;
    if (!L && !R) return 0;
    if (R) if ((l + (r - 1)) / 2 >= n) return 1;
    if (L) if (((l + 1) + r) / 2 <= n) return 1;
    return 0;
}
int main()
{
    scanf("%d", &n);
    for (int i = 1; i < n * 2; ++ i) scanf("%d", &ai[i]);
    int l = 1, r = n * 2 - 1;
    while (l < r)
    {
        int m = (l + r + 1) / 2;
        if (check(m)) l = m; else r = m - 1;
    }
    printf("%d\n", r);
}

