    int mid;
    while (lo != hi) {
      if (hi - lo == 1) {
        if (f(lo)) {
          if (f(hi)) lo = hi;
          break;
        }
        else {
          lo = lo - 1;
          break;
        }
      }
      mid = (lo + hi) / 2;
      if (f(mid)) lo = mid;
      else        hi = mid - 1;
    }
