bool chk()
{
	for(int i = 1; i <= 2*N-1; i++) F[i] = -1;
	for(int i = 1; i+1 <= 2*N-1; i++){
		if( E[i] == E[i+1] ) F[i] = F[i+1] = E[i];
	}
	pii l = pii(-1, -1), r = pii(-1, -1);
	for(int i = N; i >= 1; i--) if( F[i] != -1 ){
		l = pii(N-i, E[i]);
		break;
	}
	for(int i = N; i <= 2*N-1; i++) if( F[i] != -1 ){
		r = pii(i-N, E[i]);
		break;
	}
	if( F[N] != -1 ) return E[N];
	if( l.first == -1 || r.first == -1 ){
		if( l.first == -1 && r.first == -1 ) return E[1];
		else if( l.first == -1 ) return r.second;
		else return l.second;
	}
	return l.first < r.first? l.second : r.second;
}

