inline bool check(int limit) {
  rep(i, 0, n) b[i] = a[i] >= limit;
  dead.set();
  rep(i, 0, n) if ((i && b[i] == b[i - 1]) || i < n - 1 && b[i] == b[i + 1])
      dead[i] = false;
  if (!dead[n / 2]) return b[n / 2];
  int lptr = n / 2;
  int rptr = lptr;
  while (~lptr && dead[lptr]) lptr--;
  while (rptr < n && dead[rptr]) rptr++;
  if (lptr == -1 && rptr == n) return b[0];
  if ((rptr - lptr - 1 & 1) == 0)
    return (n / 2 - lptr) > (rptr - n / 2) ? b[lptr + 1] : b[rptr - 1];
  return !b[lptr + 1];
}

