int N;
int A[200010];

bool judge(int mid)
{
    bool k[200020] = {0},f[200020] = {0},F = false;
    if(A[0] >= mid)k[0] = true;
    else k[0] = false;

    for(int i = 1; i < N * 2 - 1; i++)
    {
        if(A[i] >= mid)k[i] = true;
        else k[i] = false;
        if(k[i] == k[i - 1])
        {
            f[i] = f[i - 1] = true;
            F = true;
        }
    }

    if(!F)return k[N - 1] ^ (N % 2 == 0);

    for(int i = 0; i < N; i++)
    {
        if(f[N - 1 + i])return k[N - 1 + i];
        if(f[N - 1 - i])return k[N - 1 - i];
    }
    return false;
}

