    for(int i = 0; i < N * 2 - 1; i++)scanf("%d",&A[i]);

    int l = 1,r = N * 2;
    while(r - l > 1)
    {
        int mid = (l + r) / 2;
        if(judge(mid))l = mid;
        else r = mid;
    }
    printf("%d\n",l);
    return 0;
}

