bool calc(ll mid){
    vector<ll> v;
    //p(mid);
    for(ll i=0;i<n;i++){
        v.pb(x[i] >= mid);
    }
    ll l, r;
    if(v[(n-1)/2] == 1){
        l = (n-1)/2, r = (n-1)/2;
    }else{
        l = (n-1)/2 + 1, r = (n-1)/2 - 1;
    }
    while(true){
        if(v[l-1] == 0 && v[l-2] == 1 && l-2 >= 0){
            l -= 2;
        }else{
            break;
        }
    }
    while(true){
        if(v[r+1] == 0 && v[r+2] == 1 && r+2 < n){
            r += 2;
        }else{
            break;
        }
    }
    //pe(l);p(r);
    if(!(l <= (n-1)/2 && (n-1)/2 <= r)){
        return (v[(n-1)/2] == 1);
    }
    if(l == 0 && r == n - 1){
        return true;
    }
    if((l + r) / 2 == (n-1)/2){
        return (v[r+1] == 1) || (v[l-1] == 1);
    }else{
        ll center = (l + r) / 2;
        if(center < (n-1)/2){
            return (v[r+1] == 1);
        }else{
            return (v[l-1] == 1);
        }
    }
}

void solve(){
    cin >> n;
    n = 2 * n - 1;
    for(ll i=0;i<n;i++){
        cin >> x[i];
    }
    if(n <= 3000){
        vector<ll> v, u;
        for(ll i=0;i<n;i++){
            v.pb(x[i]);
        }
        while(n > 1){
            for(ll i=0;i<n-2;i++){
                y[0] = v[i], y[1] = v[i+1], y[2] = v[i+2];
                sort(y, y + 3);
                u.pb(y[1]);
            }
            v.clear();
            for(ll i=0;i<u.size();i++){
                v.pb(u[i]);
            }
            u.clear();
            n -= 2;
        }
        p(v[0]);
        return;
    }
    // n-1
    ll ok = 1, ng = n + 1;
    while(ng - ok > 1){
        ll mid = (ok + ng) / 2;
        bool res = calc(mid);
        if(res){
            ok = mid;
        }else{
            ng = mid;
        }
    }
    p(ok);



    return;
}

