void solve(long long N, std::vector<long long> a){
  ll l=-1,r=N+1;
  while(l+1<r){
    ll mid=(l+r)/2;
    vector<int> x(a.size());
    REP(a.size()){
      if(a[i]>=mid){
        x[i]=1;
      }else{
        x[i]=0;
      }
    }
    ll bl=a.size()/2,br=a.size()/2;
    while(bl>0){
      if(x[bl]==x[bl-1]){
        break;
      }
      --bl;
    }
    if(bl==a.size()/2&&x[bl]==1){
      l=mid;
      continue;
    }
    while(br>0){
      if(x[br]==x[br+1]){
        break;
      }
      ++br;
    }
    ll tmp;
    if(a.size()/2-bl<br-a.size()/2){
      tmp=x[bl];
    }else{
      tmp=x[br];
    }
    if(tmp==1){
      l=mid;
    }else{
      r=mid;
    }
  }
  cout<<l<<endl;
}

