class Main {
public:
	int topNum(vector<int>& as) {

		vector<int> oldVec = as;
		vector<int> newVec;
		for (int i = (as.size() + 1) / 2;i >= 2;i--) {
			newVec.clear();
			for (int j = 1;j <= oldVec.size() - 2;j++) {
				newVec.push_back(median3(oldVec[j - 1], oldVec[j], oldVec[j + 1]));
			}
			oldVec = newVec;
		}

		return newVec[0];
	}

	int median3(int x, int y, int z)
	{
		if (x < y) {
			if (y < z) {
				return y;
			}
			else if (z < x) {
				return x;
			}
			return z;
		}
		else {
			if (z < y) {
				return y;
			}
			else if (x < z) {
				return x;
			}
			return z;
		}
	}
};


