bool isval(int x){
	for (int i = 1; i <= n; i++){
		b[i] = (a[i] >= x);
	}
	int L = 0, R = n + 1;
	for (int i = n / 2 + 1; i >= 2; i--){
		if (b[i] == b[i - 1]){
			L = i;
			break;
		}
	}
	for (int i = n / 2 + 1; i < n; i++){
		if (b[i] == b[i + 1]){
			R = i;
			break;
		}
	}
	if (L == 0 && R == n + 1){
		return (b[n / 2 + 1] ^ (((n + 1) / 2 + 1) % 2));
	}
	if (L == 0){
		return b[R];
	}
	if (R == n + 1){
		return b[L];
	}
	if ((n / 2 + 1) - L < R - (n / 2 + 1)){
		return b[L];
	}
	return b[R];
}

