bool check(int mid)
{
	int mi=1e9,j=-1;
	for(int i=m-1;i>=1;i--)
	{
		int a1=a[i]>=mid,a2=a[i+1]>=mid;
		if(a1==a2)
		{
			mi=m-i-1;
			j=a1;
			break;
		}
	}
	for(int i=m;i<n;i++)
	{
		int a1=a[i]>=mid,a2=a[i+1]>=mid;
		if(a1==a2)
		{
			if(mi>i-m)
			{
				mi=i-m;
				j=a1;
			}
			break;
		}
	}
	if(j==-1)return a[1]>=mid;
	else return j;
}
