	cin>>n;
	for(int i=0;i<2*n-1;i++)
		cin>>a[i];
	vector<int> b(2*n-1);
	for(int i=0;i<n-1;i++)
	{
		for(int j=n-1;i+1<=j;j--)
		{
			b[j]=median(a[j-1],a[j],a[j+1]);
			if(b[j]==b[j+1])
				break;
		}
		for(int j=n-1;j<(2*n-1)-(i+1);j++)
		{
			b[j]=median(a[j-1],a[j],a[j+1]);
			if(b[j-1]==b[j])
				break;
		}
		a.swap(b);
	}
	cout<<a[n-1]<<endl;
}

