  REP(i, 0, cont.size() + 1) {
    if (i >= cont.size() || cont[i].second != 1) {
      if (cnt >= 1) {
	cont2.push_back(PI(-1, cnt));
	cnt = 0;
      }
    }
    if (i < cont.size()) {
      PI t = cont[i];
      if (t.second == 1) {
	cnt++;
      } else {
	cont2.push_back(t);
      }
    }
  }
  if (DEBUG) {
    cerr << "cont:" << endl;
    REP(i, 0, cont.size()) {
      cerr << (cont[i].first ? "b" : "w") << cont[i].second << " ";
    }
    cerr << endl << "cont2:" << endl;
    REP(i, 0, cont2.size()) {
      cerr << (cont2[i].first == -1 ? "m" : cont2[i].first ? "b" : "w")
	   << cont2[i].second << " ";
    }
    cerr << endl;
  }
  if (cont2.size() == 1 && cont2[0].first == -1) { // (w)bwbwbwbw...
    return v[0];
  }
  REP(i, 0, cont2.size()) {
    if (cont2[i].first >= 0) {
      continue;
    }
    int stripe = cont2[i].second;
    if (i >= 1) {
      cont2[i - 1].second += stripe / 2;
      cont2[i - 1].second -= stripe / 2;
    }
    if (i < cont2.size() - 1) {
      cont2[i + 1].second += stripe - stripe / 2;
      cont2[i].second -= stripe - stripe / 2;
    }
  }
  if (DEBUG) {
    cerr << "cont2-mod:" << endl;
    REP(i, 0, cont2.size()) {
      cerr << (cont2[i].first == -1 ? "m" : cont2[i].first ? "b" : "w")
	   << cont2[i].second << " ";
    }
    cerr << endl;
  }
  int rem = v.size() / 2;
  REP(i, 0, cont2.size()) {
    if (rem < cont2[i].second) {
      assert (cont2[i].first >= 0);
      return cont2[i].first;
    }
    rem -= cont2[i].second;
  }
  assert (0);
}

