int solve(int val)
{
	for (int i = 0; i < 2 * n - 1; i++)
		b[i] = (a[i] >= val);

	/*cerr << "SOLVE " << val << endl;
	for (int i = 0; i < 2 * n - 1; i++)
		cerr << '_' << b[i];
	cerr << endl;*/

	int pos = n - 1;
	if (b[pos] == b[pos - 1] || b[pos] == b[pos + 1])
		return b[pos];

	int LEFT = pos, RIGHT = pos;
	for (int i = pos + 1; i < 2 * n - 1; i++)
		if (b[i] != b[i - 1])
			RIGHT = i;
	for (int i = pos - 1; i >= 0; i--)
		if (b[i] != b[i + 1])
			LEFT = i;

	if (LEFT == 0 && RIGHT == 2 * n - 2)
		return b[0];
	if (LEFT == 0)
		return b[RIGHT];
	if (RIGHT == 2 * n - 2)
		return b[LEFT];
	if (b[LEFT] == b[RIGHT])
		return b[LEFT];
	if (abs(pos - LEFT) < abs(pos - RIGHT))
		return b[LEFT];
	return b[RIGHT];
}

