int med(int&a ,int&b ,int&c)
{
    if(min(a ,c) <= b && b <= max(a ,c)) return b;
    if(min(b ,c) <= a && a <= max(b ,c)) return a;
    if(min(b ,a) <= c && c <= max(b ,a)) return c;
}

int Solve(vector<int>A)
{
    vector <int> B = A;

    for(int j=1; j<N; j++ ,swap(A ,B))
    for(int i=j; i<((int)A.size())-j; i++)
        B[i] = med(A[i-1] ,A[i] ,A[i+1]);

    return A[N-1];
}

