	int md = n / 2;
	if (mark[md] == mark[md + 1] || mark[md] == mark[md + 1])
	{
		if (mark[md] == true)
		{
			return true;
		}
		else
		{
			return false;
		}
	}
	int check = true;
	for (int i = 0; i < n - 1; i++)
	{
		if (mark[i] == mark[i + 1])
		{
			check = false;
		}
	}
	if (check)
