		}
		if (mark[md] && h % 2 == 0)
		{
			return false;
		}
		if (!mark[md] && h % 2 == 0)
		{
			return true;
		}
		if (!mark[md] && h % 2 == 1)
		{
			return false;
		}
	}
	for (int i = md, j = md; i < n && j >= 0; i++, j--)
	{
		if (mark[i] == mark[i + 1])
		{
			if (mark[i] == true)
			{
				return true;
			}
			else
			{
				return false;
			}
		}
		if (mark[j] == mark[j - 1])
		{
			if (mark[j] == true)
			{
				return true;
			}
			else
			{
				return false;
			}
		}
	}
}

