bool boleh(int k){
    for(int i = 0; i < 2*N-1; i ++){
        b[i] = (a[i] < k);
    }
    c[0] = b[0];
    c[2*N-2] = b[2*N-2];

    queue<int> q;
    for(int i = 1; i < 2*N-2; i ++){
        if(b[i+1] == b[i-1] && b[i] != b[i+1]){
            c[i] = 3;
        }else{
            c[i] = b[i];
            q.push(i);
        }
    }

    while(!q.empty()){
        int u = q.front(); q.pop();
        //printf("visited %d c[%d]=%d\n", u, u, c[u]);

        if(u < 2*N-1 && c[u+1] == 3){
            c[u+1] = c[u];
            q.push(u+1);
        }

        if(u > 0 && c[u-1] == 3){
            c[u-1] = c[u];
            q.push(u-1);
        }
    }

    return c[N-1];
}

