const int N=1e5+5;

int n,a[N];

bool can(int u){
	for(int i=0;i<n;i++){
		if(a[n+i]<=u&&a[n+i+1]<=u){
			return true;
		}
		if(a[n-i]<=u&&a[n-i-1]<=u){
			return true;
		}
		if(a[n+i]>u&&a[n+i+1]>u){
			return false;
		}
		if(a[n-i]>u&&a[n-i-1]>u){
			return false;
		}
	}
}

int bs(int l,int r){
	if(l==r){
		return l;
	}
	int mid=(l+r)>>1;
	if(can(mid)){
		return bs(l,mid);
	}
	else{
		return bs(mid+1,r);
	}
}

