using namespace std;
typedef long long ll;
typedef pair<int,int> P;
typedef pair<int,P> PP;
int n;
int a[200005];
int b[200005];

bool C(int v){
	for(int i=0;i<n*2-1;i++){
		if(a[i]<v){
			b[i]=0;
		}else{
			b[i]=1;
		}
	}
	vector<PP> vec;
	int cnt=1;
	for(int i=1;i<n*2-1;i++){
		if(b[i-1]==b[i])cnt++;
		else{
			if(cnt>=2){
				vec.push_back(PP(i-1,P(b[i-1],cnt)));
				cnt=1;
			}
		}
	}
	if(cnt>=2){
		vec.push_back(PP(n*2-2,P(b[n*2-2],cnt)));
	}
	if(vec.size()==0){
		int c=n-1;
		return (((c+b[n-1])%2)==1);
	}
	cnt=1;
	bool flag2=false;
	for(int i=1;i<n*2-1;i++){
		if(b[i-1]!=b[i]){
			cnt++;
		}else{
			if(!flag2 && cnt>=2){
				vec[0].second.second+=cnt-1;
			}
			if(flag2 && cnt>=3){
				int l=lower_bound(vec.begin(),vec.end(),PP(i,P(0,0)))-vec.begin();
				if(cnt%2==0){
					vec[l-1].second.second+=(cnt-2)/2;
					vec[l].second.second+=(cnt-2)/2;
				}else{
					vec[l-1].second.second+=cnt-2;
				}
			}
			flag2=true;
			cnt=1;
		}
	}
	int now=0;
	for(int i=0;i<vec.size();i++){
		now+=vec[i].second.second;
		if(now>=n)return (vec[i].second.first==1);
	}
	return false;
}

int main(void){
	scanf("%d",&n);
	for(int i=0;i<n*2-1;i++){
		scanf("%d",&a[i]);
		a[i]--;
	}
	int l=0,r=2*n-1;
	while(l+1<r){
		int mid=(l+r)/2;
		if(C(mid))l=mid;
		else r=mid;
	}
	printf("%d\n",l+1);
	return 0;
}

