bool isval(int x) {
    for (int i = 0; i <= 2 * n; i++) a[i] = (p[i] >= x);
    if (a[n] == a[n-1] || a[n] == a[n+1]) return a[n];
    int l = n-1, r = n+1;
    while (a[l] != a[l-1]) l--;
    while (a[r] != a[r+1]) r++;
    l++, r--;
    if ((r - l + 1) % 2) return a[l-1];
    if (n <= (r+l)/2) return a[l-1];
    else return a[r+1];

}
