bool judge(int x){
	REP(i,1,n*2-1)c[i]=(a[i]>x);
	if(c[n]==c[n-1] || c[n]==c[n+1])return !c[n];
	int L=n,R=n,len;
	while(L>2 && c[L-1]!=c[L-2] && c[L-1]!=c[L])--L;
	while(R<n*2-2 && c[R+1]!=c[R+2] && c[R+1]!=c[R])++R;
	len=min(n-L+1,R-n+1);
	if(len%2)return c[n];
	return !c[n];
}

