const int Max_N(100050);

int N, A[Max_N << 1];
int Tot, L[Max_N], R[Max_N];

int work(const int &K)
{
	Tot = 0;
	for (int i = 1, j = 1;i <= 2 * N - 1;++i)
		if (i == 2 * N - 1 || (A[i + 1] > K) != (A[i] > K))
		{
			if (j < i)
			{
				++Tot, L[Tot] = j, R[Tot] = i;
				if (L[Tot] <= N && N <= R[Tot])
					return A[i] > K;
			}
			j = i + 1;
		}
	if (Tot == 0)
		return A[1] > K;
	int a(-1), b(-1);
	for (int i = 1;i <= Tot;++i)
		if (R[i] < N)
			a = R[i];
	for (int i = Tot;i >= 1;--i)
		if (L[i] > N)
			b = R[i];
	if (a == -1 || b == -1)
		return A[a + b + 1] > K;
	if ((A[a] > K) == (A[b] > K))
		return A[a] > K;
	else
		return N - a < b - N ? A[a] > K : A[b] > K;
}

