int n;
vector<int> p, v;

int getMean(int a, int b, int c){
	vector<int> abc;
	abc.pb(a);
	abc.pb(b);
	abc.pb(c);
	sort(abc.begin(), abc.end());
	return abc[1];
}

pii zigzag(vector<int> v){
	pii ret = mp(1, 0);
	rep(i, 1, sz(v)) if(v[i] == v[i-1]) ret.first = false;
	if(!ret.first) return ret;
	int change = ((n / 2) & 1);
	ret.second = (v[n/2] ^ change);
	return ret;
}

int check(int val){
	v.clear();
	rep(i, 0, n){
		int value;
		if(p[i] >= val) value = 1;
		else value = 0;
		v.pb(value);
	}
	pii ret = zigzag(v);
	if(ret.first) return ret.second;

	int lp = -1;
	int rp = -1;
	per(i, n/2 + 1, 1) if(v[i] == v[i-1]){
		lp = i;
		break;
	}
	rep(i, n/2, n-1) if(v[i] == v[i+1]){
		rp = i;
		break;
	}
	int addLp = 0, addRp = 0;
	if(lp == -1 || rp == -1){
		if(lp == -1) return v[rp];
		else return v[lp];
	}else{
		if(v[lp] == v[rp]) return v[lp];
		rep(i, lp+1, rp) if(v[i] == v[lp]) ++addLp;
		else --addRp;
	}
	if(lp + addLp >= n/2) return v[lp];
	else return v[rp];
}

int brute(vector<int> a){
	vector<int> na;
	while(sz(a) > 1){
		na.clear();
		rep(i, 1, sz(a) - 1){
			na.pb(getMean(a[i-1], a[i], a[i+1]));
		}
		a = na;
	}
	return a[0];
}

void solve(){
	cin >> n;
	n = n * 2 - 1;
	rep(i,0,n){
		int v;
		cin >> v;
		p.pb(v);
	}

	int l = 0;
	int r = n + 1;
	while(r-l>1){
		int m = (l+r)/2;
		if(check(m)) l = m;
		else r = m;
	}
	cout << l << endl;
	//cout << brute(p) << endl;
	//assert(l == brute(p));
}

