inline bool check(int x) {
	For(i, 1, n) t[i] = a[i] <= x;

	for (register int i = 0, x, y; i < tt - 1; ++ i) {
		x = tt - i, y = x + 1;
		if (t[x] == 1 && t[y] == 1) return true;
		if (t[x] == 0 && t[y] == 0) return false;

		x = tt + i, y = tt + i + 1;
		if (t[x] == 1 && t[y] == 1) return true;
		if (t[x] == 0 && t[y] == 0) return false;
	}

