    int large = n * 2;
    while (small + 1 < large) {
      int mid = (small + large) / 2;
      if (pred(mid, a, n)) small = mid;
      else large = mid;
    }
    if (pred(large, a, n)) cout << large << endl;
    else if (pred(small, a, n)) cout << small << endl;
    else assert(false);
  }
  return 0;
}

