int median(int a, int b, int c) {
    return a ^ b ^ c ^ min({a, b, c}) ^ max({a, b, c});
}

signed main() {
    ios_base::sync_with_stdio(false); cin.tie(0);

    int n;
    cin >> n;
    int k = 2 * n - 1;
    vector<int> a(k + 2);
    vector<int> b(k + 2);
    for (int i = 1; i <= k; ++i) {
        cin >> a[i];
    }

    int left = 1, right = k;
    for (int step = n; step > 1; --step) {
        left = max(left, n - step + 1);
        right = min(right, k - (n - step));
        for (int i = left + 1; i <= n; ++i) {
            if (a[i] == median(a[i - 1], a[i], a[i + 1])) {
                left = i - 1;
            }
        }
        for (int i = right - 1; i >= n; --i) {
            if (a[i] == median(a[i - 1], a[i], a[i + 1])) {
                right = i + 1;
            }
        }
        for (int i = left + 1; i < right; i++) {
            if (i != n) a[i] = median(a[i - 1], a[i], a[i + 1]);
        }
        a[left - 1] = 0;
        a[right + 1] = k + 1;
        for (int i = left; i <= right; ++i) {
            b[i] = median(a[i - 1], a[i], a[i + 1]);
        }
        for (int i = left; i <= right; ++i) {
            a[i] = b[i];
        }
    }

    cout << a[n] << '\n';
}

