{
	if(a<=b && b<=c) return b;
	if(a<=c && c<=b) return c;
	if(b<=a && a<=c) return a;
	if(b<=c && c<=a) return c;
	if(c<=a && a<=b) return a;
	if(c<=b && b<=a) return b;
}

