int median(int a, int b, int c) {
  int sort[3] = {a, b, c};
  for (int i = 0; i < 2; i++) {
    for (int j = 1; j < 3 - i; j++) {
      if (sort[j] < sort[j-1]) {
        int tmp = sort[j-1];
        sort[j-1] = sort[j];
        sort[j] = tmp;
      }
    }
  }
  return sort[1];
}

void fill(vector<int> &a, int n, int i, int j) {
  if (i < j) {
    vector<int> tmp(2*n - 1, 0);
    for(int k = i; k < j; k++) {
      tmp[k] = median(a[k-1], a[k], a[k+1]);
    }
    a = tmp;
    fill(a, n, i + 1, j - 1);
  }
}

