inline bool check (int x) {
	int lt = 0;
	for (int i = 1; i <= 2 * N - 1; i++) {
		int r = i;
		while (r < 2 * N - 1 && (p[r + 1] >= x) == (p[i] >= x)) r++;
		if (r == i) continue;
