using namespace std;

int n, s[200005];
bool bin[200005];

bool good(int mid){
	FI(i, 1, n * 2 - 1){
		if(s[i] >= mid) bin[i] = 1;
		else bin[i] = 0;
	}
	//merge 0-1 array using "median" operator N - 1 times
	if(bin[n] == bin[n - 1] || bin[n] == bin[n + 1]) return bin[n];
	FI(i, 1, n - 2){
		if(bin[n - i] == bin[n - i + 1]) return bin[n - i];
		if(bin[n + i] == bin[n + i + 1]) return bin[n + i];
	}
	return bin[1];
}

int main(){
	scanf("%d", &n);
	FI(i, 1, n * 2 - 1) scanf("%d", &s[i]);
	int l = 1, r = n * 2 - 1;
	while(r - l > 1){
		int m = (l + r) >> 1;
		if(good(m)) l = m;
		else r = m;
	}
	printf("%d\n", l);
	return 0;
}

