int ai[N], bi[N];
int check(int t)
{
    for (int i = 1; i < 2 * n; ++ i) bi[i] = ai[i] >= t;
    int mid = 1e9, res = bi[n] ^ !(n & 1);
    for (int i = n - 1; i >= 1; -- i)
        if (bi[i] == bi[i + 1] && n - i < mid)
        {
            mid = n - i;
            res = bi[i];
        }
    for (int i = n + 1; i < n * 2; ++ i)
        if (bi[i] == bi[i - 1] && i - n < mid)
        {
            mid = i - n;
            res = bi[i];
        }
    return res
;}
int main()
{
    scanf("%d", &n);
    for (int i = 1; i < n * 2; ++ i) scanf("%d", &ai[i]);
