int C(int a,int b,int c)
{
	int y[3]={a,b,c};
	sort(y,y+3);
	return y[1];
}
int a[N],b[N],n;
int main()
{
	cin>>n;
	for(int i=0;i<2*n-1;i++)cin>>a[i];
	for(int i=1;i<n;i++)
	{
		for(int j=n-1;j>=i;j--)
		{
			b[j]=C(a[j-1],a[j],a[j+1]);
			if(b[j]==b[j+1])break;
		} 
		for(int j=n-1;j<2*n-1-i;j++)
		{
			b[j]=C(a[j-1],a[j],a[j+1]);
			if(b[j]==b[j-1])break;
		} 
