const int maxn = (int)(2e5) + 5;
int a[maxn], b[maxn];
int n;

bool chk(int x) {
	for (int i = 1; i <= 2 * n - 1; ++i) b[i] = (a[i] >= x);
	if (b[n] == b[n - 1] || b[n] == b[n + 1]) return b[n]; 
	int l = n, r = n;
	while (l > 1 && b[l] != b[l - 1]) --l; 
	while (r < 2 * n - 1 && b[r] != b[r + 1]) ++r;
	if (b[l] == b[r]) return b[l]; 
	return n - l < r - n ? b[l] : b[r];
}

