int n;
int len;
int a[234567];

struct block
{
	enum st{ SOLID0, SOLID1, MESH0, MESH1, HALF0, HALF1 }state;
	int length;
};

bool deck[234567];
bool condition(int x)
{
	for (int i = 0; i < len; ++i)
	{
		deck[i] = (a[i] >= x);
	}
	bool flag = true;
	for (int i = 1; i < len; ++i)
	{
		if (deck[i] == deck[i - 1])
		{
			flag = false;
			break;
		}
	}
	if (flag)return deck[0];
	vector<block> fold, real;
	int size = 1;
	for (int i = 1; i < len; ++i)
	{
		if (deck[i] != deck[i - 1])
		{
			fold.push_back({ deck[i - 1] ? block::SOLID1 : block::SOLID0 ,size });
			size = 1;
		}
		else
		{
			++size;
		}
	}
	fold.push_back({ deck[len - 1] ? block::SOLID1 : block::SOLID0 ,size });
	for (auto itr = fold.begin(); itr != fold.end();)
	{
		auto move = itr;
		while (move != fold.end() && move->length == 1)
		{
			++move;
		}
		if (move != itr)
		{
			block tmp;
			tmp.length = move - itr;
			if (tmp.length % 2 == 0)
			{
				tmp.state = ((itr->state == block::SOLID0) ? block::HALF0 : block::HALF1);
			}
			else
			{
				tmp.state = ((itr->state == block::SOLID0) ? block::MESH0 : block::MESH1);
			}
			real.push_back(tmp);
			itr = move;
		}
		else
		{
			real.push_back(*itr);
			++itr;
		}
	}
	int xepher = 0;
	for (const auto& elm : real)
	{
		xepher += elm.length;
		if (xepher >= n - 1)
		{
			switch (elm.state)
			{
			case block::SOLID0:
			case block::SOLID1:
				return (elm.state == block::SOLID1);
				break;
			case block::MESH0:
			case block::MESH1:
				return (elm.state == block::MESH0);
				break;
			case block::HALF0:
			case block::HALF1:
			{
				int cas = xepher - elm.length / 2;
				return (cas > n - 1) == (elm.state == block::HALF0);
			}
				break;
			default:
				assert(false);
				return true;
				break;
			}
		}
	}
}

