bool low[N];
bool is(int l, int r) {
  if(r < l) swap(l, r);
  if(r >= n) return 0;
  return bit.range(l + 1, r) == r - l;
}

void check(int i) {
  if(i < 0 || i >= n) return;
  bit.set(i, low[i] != low[i - 1]);
}

