const int MAX_N = 100005;

int A[MAX_N << 1], N, B[MAX_N << 1];

bool check(int mid) {
  int len = N * 2 - 1;
  for (int i = 1; i <= len; ++i)
    B[i] = A[i] >= mid;

  int L = 0, R = N + 1;

  for (int i = 1, j; i <= len; ++i) {
    j = i;
    while (j < len && B[j + 1] == B[i]) j++;

    if (j - i >= 1 && i <= N && j >= N) return B[i];
    if (j - i >= 1) {
      if (j <= N) L = max(L, j);
      if (i >= N) R = min(R, i);
    }

    i = j;
  }

  if (L != 0 && R != N + 1) {
    return N - L > R - N ? B[R] : B[L];
  } else if (L != 0) {
    return B[L];
  } else if (R != 0) {
    return B[R];
  } else {
    return B[1];
  }
}

int main() {
  scanf("%d", &N);
  for (int i = 1; i < N << 1; ++i)
    scanf("%d", &A[i]);

  int l = 1, r = 2 * N - 1, res = 0;

  while (l <= r) {
    int mid = (l + r) >> 1;
    if (check(mid)) res = mid, l = mid + 1;
    else r = mid - 1;
  }

  printf("%d\n", res);
  return 0;
}

