using namespace std;
template <class T> inline void setmax(T & a, T const & b) { a = max(a, b); }
template <class T> inline void setmin(T & a, T const & b) { a = min(a, b); }

int median(int a, int b, int c) {
    if (a > b) swap(a, b);
    return max(a, min(b, c));
}
int main() {
    // input
    int n; scanf("%d", &n);
    vector<int> a(2 * n - 1);
    repeat (i, 2 * n - 1) {
        scanf("%d", &a[i]);
    }
    // solve
    int l = 0;
    int r = 2 * n - 1;
    vector<int> b(2 * n - 1);
    repeat (i, n - 1) {
        setmax(l, i + 1);
        setmin(r, 2 * n - 1 - (i + 1));
        repeat_from (j, l, r) {
            b[j] = median(a[j - 1], a[j], a[j + 1]);
            if (b[j] <= n - 1 and b[j - 1] == b[j]) setmax(l, j + 1);
            if (b[j] >= n - 1 and b[j + 1] == b[j]) setmin(r, j);
        }
        a.swap(b);
    }
    // output
    printf("%d\n", a[n - 1]);
    return 0;
}

