int mid(int x, int id) {
	int aa = a[x-1][id];
	int bb = a[x][id];
	int cc = a[x+1][id];
	if (aa == bb || aa == cc) return aa;
	if (bb == cc) return bb;
	if ((bb < aa && aa < cc) || (cc < aa && aa < bb)) return aa;
	if ((aa < bb && bb < cc) || (cc < bb && bb < aa)) return bb;
	if ((aa < cc && cc < bb) || (bb < cc && cc < aa)) return cc;
}
