		r --;
		pre = !pre;
		cur = !cur;
		int i = l;
		while (i <= r) {
			a[i][cur] = mid(i, pre);
			if (a[i][cur] == a[i-1][cur] && i <= n) l = i;
			if (a[i][cur] == a[i+1][cur] && i >= n) {
				r = i;
				break;
			}
