bool testmincase(int n,int m) {
	if (isp[n])return false;
	vector<bool> used(n, false);
	queue<int> q; q.push(0); used[0] = true;
	rep(i, n) {
		if (isp[i + 1])used[i] = true;
	}
	while (!q.empty()) {
		int id = q.front(); q.pop();
		if (id%m) {
			if (!used[id - 1]) {
				used[id - 1] = true; q.push(id - 1);
			}
		}
		if ((id%m) < m - 1 && id < n - 1) {
			if (!used[id + 1]) {
				used[id + 1] = true; q.push(id + 1);
			}
		}
		if (id - m >= 0) {
			if (!used[id - m]) {
				used[id - m] = true; q.push(id - m);
			}
		}
		if (id + m < n) {
			if (!used[id + m]) {
				used[id + m] = true; q.push(id + m);
			}
		}
	}
	return used[n - 1];
}
int a[1 << 18]; int n;
bool can(int x) {
	vector<int> b(2 * n - 1);
	rep(i, 2 * n - 1) {
		if (a[i] < x)b[i] = 0;
		else b[i] = 1;
	}
	int le = 0, ri = 2 * n - 2;
	for (int i = n - 1; i > 0; i--) {
		if (b[i] == b[i - 1]) {
			le = i; break;
		}
	}
	for (int i = n - 1; i < 2 * n - 2; i++) {
		if (b[i] == b[i + 1]) {
			ri = i; break;
		}
	}
	if (b[le] == b[ri])return b[le];
	if (n - 1 - le < ri - (n - 1))return b[le];
	else return b[ri];
}
