const int maxn=2e5+10;

int n,a[maxn];
bool x[maxn];

bool pass(int mid)
{
	int i,l=0,r=2*n;;
	for (i=1;i<2*n;i++)
		x[i]=a[i]<=mid;
	if (x[n]==x[n-1]) return x[n];
	if (x[n]==x[n+1]) return x[n];
	for (i=2*n-1;i>n;i--)
		if (x[i]==x[i-1]) r=i;
	for (i=1;i<n;i++)
		if (x[i]==x[i+1]) l=i;
	if (n-l<r-n) return x[l];
	if (n-l>r-n) return x[r];
	return !x[n]^(n&1);
}

