int N, M;
vi a, b;

int f(int x) {
    rep(i, M)b[i] = a[i] >= x;
    b[M] = 1 - b[M - 1];

    int dis = INT_MAX, val=-1, l = 0, cur = b[0];
    rep(r, M + 1) {
        if(b[r] != cur) {
            int len = r - l;
            if(len >= 2) {
                if(l <= N - 1 && N - 1 < r) {
                    val = cur;
                    break;
                }
                if(N - 1 < l && l <= 2 * (N - 1) && l - (N - 1) < dis) {
                    dis = l - (N - 1);
                    val = cur;
                }
                if(r <= N - 1 && r >= 1 && N - 1 - (r - 1) < dis) {
                    dis = N - 1 - (r - 1);
                    val = cur;
                }
            }
            l = r;
            cur = b[r];
        }
    }
    if(val != -1)return val;
    else return (b[N - 1] + N - 1) & 1;
}

