#include <bits/stdc++.h>

using namespace std;
const int N = 1e5 + 5;

int nn, n;
int a[N];

int small(int x, int y, int k) {
	return (a[x] <= k && a[y] <= k);
}

int big(int x, int y, int k) {
	return (a[x] > k && a[y] > k);
}

bool check(int k) {
	if (small(n, n - 1, k) || small(n, n + 1, k)) return 0;
	if (big(n, n - 1, k) || big(n, n + 1, k)) return 1;
	for (int i = 1; i < n - 1; i ++) {
		if (small(n - i, n - i - 1, k) || small(n + i, n + i + 1, k)) return 0;
		if (big(n - i, n - i - 1, k) || big(n + i, n + i + 1, k)) return 1;
	}
	return small(1, 1, k);
}

int main() {
	scanf("%d", &n);
	nn = 2 * n - 1;
	for (int i = 1; i <= nn; i ++) scanf("%d", &a[i]);
	int l = 1, r = nn, pos;
	while (l <= r) {
		int mid = (l + r) >> 1;
		if (check(mid)) l = mid + 1, pos = mid;
		else r = mid - 1;
	}
	printf("%d\n", a[pos + 1]);
	return 0;	
}

